mapper.select:

	select count(*) from bookmark 
 	where userno = #{userNo} 
 		  and (title like  '%${keyword}%' or description like '%${keyword}%' or keyword like '%${keyword}%' )


		select count(*) from bookmark 
 		where link = #{link} 
 		 	  and userNo=#{userNo}


	select link from bookmark 
 	where userno = #{userNo} 
 	      and (title like  '%${keyword}%' or description like '%${keyword}%' or keyword like '%${keyword}%' )

select userno, wordno, title, date_format(regDate, '%Y-%m-%d') as regDate, link, keyword, thumbnail, description, bmkListNo, (select title from bookmarklist where bmkListNo = bookmark.bmkListNo) as bmkListName from bookmark
AND bmkListNo=#{bmkListNo}
AND userno = #{userNo} and (title like '%${keyword}%' or description like '%${keyword}%' or keyword like '%${keyword}%' )
AND userno = #{userNo} and keyword like '%${keyword}%'
AND userno = #{userNo} and (title like '%${keyword}%' or description like '%${keyword}%' )
order by wordNo desc limit #{startRow},#{showNum}

select userno, wordno, title, date_format(regDate, '%Y-%m-%d') as regDate, link, keyword, thumbnail, description, bmkListNo from bookmark 


			 AND bmkListNo=#{bmkListNo} 



			 AND userno = #{userNo} and  (title like  '%${keyword}%' or description like '%${keyword}%' or keyword like '%${keyword}%' )



			 AND userno = #{userNo} and  keyword like '%${keyword}%'	



			 AND  userno = #{userNo} and (title like  '%${keyword}%' or description like '%${keyword}%' )	


order by wordNo desc limit #{startRow},#{showNum}

